import React, { useState, useEffect } from 'react';
import { Text, View, ActivityIndicator, Image, ScrollView } from 'react-native';
import { TouchableOpacity } from 'react-native-gesture-handler';
import { api } from '../services';
import { theme, text } from '../styles';
import arrow from '../assets/leftArrow.png';

const ProductDetails = ({
    route: {
        params: { id },
    },
}) => {
    const [product, setProduct] = useState({
        id: null,
        name: null,
        description: null,
        price: null,
        imgUrl: null,
        date: null,
        categories: [],
    });

    const [loading, setLoading] = useState(false);

    async function loadProductData() {
        setLoading(true);
        const res = await api.get(`/products/${id}`);
        setProduct(res.data);
        setLoading(false);
    }

    useEffect(() => {
        loadProductData();
    }, []);

    return (
        <View style={theme.detailsContainer}>
            {
                loading ? (<ActivityIndicator size="large" />) :
                    <View style={theme.detailCard}>
                        <TouchableOpacity style={theme.goBackContainer}>
                            <Image source={arrow} />
                            <Text>Voltar</Text>
                        </TouchableOpacity>
                        <View style={theme.productImageContainer}>
                            <Image
                                source={{ uri: product.imgUrl }}
                                style={theme.productImage}
                            />
                        </View>
                        <Text>{product.name}</Text>
                        <View>
                            <Text>R$</Text>
                            <Text>{product.price}</Text>
                        </View>
                        <ScrollView>
                            <Text>{product.description}</Text>
                        </ScrollView>
                    </View>
            }
        </View>
    );
};

export default ProductDetails;
